(function(){"use strict";const sel=(a,b=document)=>{return b.querySelector(a)};const all=(a,b=document)=>{return[...b.querySelectorAll(a)]};const addEvt=(a,b,c)=>{if(Array.isArray(a)||typeof a==='string'){if(!Array.isArray(a)){a=all(a)};for(let e of a){e.addEventListener(b,c)}}else{a.addEventListener(b,c)}};const loaded=(a)=>{addEvt(document,'DOMContentLoaded',a)};window.sel=sel;window.all=all;window.addEvt=addEvt;window.loaded=loaded}())